CREATE CACHED TABLE "argomenti"("ID" INTEGER GENERATED BY DEFAULT AS IDENTITY(START WITH 0) NOT NULL PRIMARY KEY,"descrizione" VARCHAR(50) NOT NULL)
CREATE CACHED TABLE "domande"("ID" INTEGER GENERATED BY DEFAULT AS IDENTITY(START WITH 0) NOT NULL PRIMARY KEY,"domanda" VARCHAR(300) NOT NULL,"risp1" VARCHAR(200),"risp2" VARCHAR(200),"risp3" VARCHAR(200),"risp4" VARCHAR(200),"risp5" VARCHAR(200),"idArgomento" INTEGER,"utilizzato" BOOLEAN,CONSTRAINT SYS_FK_160 FOREIGN KEY("idArgomento") REFERENCES "argomenti"("ID") ON DELETE CASCADE ON UPDATE CASCADE)
CREATE CACHED TABLE "elaborati"("ID" INTEGER GENERATED BY DEFAULT AS IDENTITY(START WITH 0) NOT NULL PRIMARY KEY,"data" DATE,"gruppo" TINYINT,"matricolaStudente" VARCHAR(50),"punteggio" REAL,"nomeStudente" VARCHAR(50),"cognomeStudente" VARCHAR(50),"note" VARCHAR(50))
CREATE CACHED TABLE "domande_elaborati"("ID" INTEGER GENERATED BY DEFAULT AS IDENTITY(START WITH 0) NOT NULL PRIMARY KEY,"domanda" VARCHAR(300) NOT NULL,"risp1" VARCHAR(200),"risp2" VARCHAR(200),"risp3" VARCHAR(200),"risp4" VARCHAR(200),"risp5" VARCHAR(200),"idDomanda" INTEGER,"idElaborato" INTEGER,"esatta" TINYINT,"fornita" TINYINT,"esito" VARCHAR(50),CONSTRAINT SYS_FK_163 FOREIGN KEY("idDomanda") REFERENCES "domande"("ID"),CONSTRAINT SYS_FK_166 FOREIGN KEY("idElaborato") REFERENCES "elaborati"("ID") ON DELETE CASCADE ON UPDATE CASCADE)
